import { Component } from '@angular/core';
import { DentalServiceService } from '../dental-service.service';
import { Router } from '@angular/router';
import { Patients } from '../model/Patients';

@Component({
  selector: 'app-createpatient',
  templateUrl: './createpatient.component.html',
  styleUrls: ['./createpatient.component.css']
})
export class CreatepatientComponent {

  submitted = false;
  patients=new Patients();

  constructor(private service:DentalServiceService, private router: Router) { }

  onSubmit()
  {
    this.submitted=true;

    this.save();
  }

  save()
  {
    this.service.createpatient(this.patients).subscribe();
    // this.service.createPlayer(this.player).subscribe((data)=>{
    //   console.log(data);
    //   this.player=new Player();
    //   this.gotoList();
    // })
  }
  gotoList() {
    this.router.navigate(['list']);
  }
  }


}
